`timescale 1ns / 1ps


module DenseLayer_testbench;
reg signed [4095:0]  inputs;
reg clk;
reg rst;
wire signed[95:0]  outputs;
DenseLayer uut(
.inputs(inputs),
.clk(clk),
.rst(rst),
.outputs(outputs)
);
localparam SF=2.0**-8.0;
integer i;
initial begin
clk=0;
rst=0;
inputs=4096';
forever #5 clk=~clk;


end
always@(uut.bitshifted) begin
$display("Time: %0d | level1 values:", $time);

for (i = 0; i < 256; i = i + 1) begin
$display("bitshift[%d]=%f",i, uut.bitshifted[256*i+:256]*SF);

end



end
always@(uut.currmem) begin
$display("Time: %0d | level1 values:", $time);

for (i = 0; i < 256; i = i + 1) begin
$display("mem[%d]=%d",i, uut.currmem[i]);

end
end
always@(uut.currResult) begin
$display("Time: %0d | level1 values:", $time);
$display("currResult=%f", uut.currResult*SF);
end
endmodule

