`timescale 1ns / 1ps
module Neuron_testbench;
reg signed [4095:0] inputs;
reg signed [1023:0] mem;
wire signed[23:0] result;
integer i;
Neuron uut(
.inputs(inputs),
.currmem(mem),
.result(result)
);
localparam SF=2.0**-8.0;

initial begin
inputs=4096';
mem=1023';
#5 
$display("result=%f",$itor(result*SF));
end
always@(uut.bitshifted) begin
$display("Time: %0d | level1 values:", $time);

for (i = 0; i < 256; i = i + 1) begin
$display("bitshift[%d]=%f",i,$itor( uut.bitshifted[16*i+:16]*SF));

end




end
