`timescale 1ns / 1ps


module PoolingAdder_tb;
reg signed [15*15*16:0] map;
reg clk;
wire signed [23:0] res;
PoolingAdder uut(
.map(map),
.clk(clk),
.result(res)
);
localparam SF=2.0**-8.0;

initial begin
clk=1'b0;
map=3600';
forever #5 clk=~clk;
end
always@(res) begin
$display("Time: %0d | outputs:", $time);
$display("result=%f",res*SF);

end

endmodule
